@extends('frontend.layouts.master')
<style>
.hotel-about {
    padding: 80px 20px;
    background: #fff;
    font-family: 'Cairo', sans-serif;
}

.hotel-container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 50px;
    max-width: 1200px;
    margin: 0 auto;
    flex-wrap: wrap;
}

/* الصور */
.hotel-images {
    position: relative;
    flex: 1 1 450px;
}

.hotel-img {
    width: 100%;
    border-radius: 20px;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
    transition: transform 0.4s ease;
}

.hotel-img.main:hover {
    transform: scale(1.03);
}

.hotel-img.overlay {
    position: absolute;
    bottom: -30px;
    right: -30px;
    width: 60%;
    border: 6px solid #fff;
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

/* النصوص */
.hotel-text {
    flex: 1 1 400px;
}

.hotel-text h2 {
    font-size: 2.2rem;
    color: #D9EF82;
    margin-bottom: 20px;
}

.hotel-text p {
    font-size: 1rem;
    color: #444;
    line-height: 1.8;
    margin-bottom: 20px;
}

.hotel-text ul {
    list-style: none;
    padding: 0;
    margin-bottom: 30px;
}

.hotel-text ul li {
    font-size: 1rem;
    color: #333;
    margin-bottom: 10px;
    display: flex;
    align-items: center;
    gap: 5px;
}

/* الزر */
.btn {
    display: inline-block;
    background-color: #D9EF82;
    color: #fff;
    text-decoration: none;
    padding: 12px 25px;
    border-radius: 10px;
    font-weight: bold;
    transition: 0.3s;
}

.btn:hover {
    background-color: #c4e366;
}

/* ==== التجاوب ==== */
@media (max-width: 1024px) {
    .hotel-container {
        gap: 40px;
    }
}

@media (max-width: 768px) {
    .hotel-container {
        flex-direction: column;
        text-align: center;
    }

    .hotel-images {
        order: 2;
        width: 100%;
    }

    .hotel-text {
        order: 1;
    }

    .hotel-img.overlay {
        position: static;
        width: 80%;
        margin: 20px auto 0;
        display: block;
    }

    .hotel-text h2 {
        font-size: 1.8rem;
    }

    .hotel-text p {
        font-size: 0.95rem;
    }
}

@media (max-width: 480px) {
    .hotel-text h2 {
        font-size: 1.6rem;
    }

    .hotel-text ul li {
        font-size: 0.9rem;
    }

    .btn {
        padding: 10px 20px;
        font-size: 0.95rem;
    }
}
</style>

